#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

string anagramPattern(string text, string pattern)
{
    vector <int> countP(26, 0);
    vector <int> countT(26, 0);
    for ( int  i=0; i<text.size(); i++)
        countT[text[i]-97]++;
    for (int  i=0; i<pattern.size(); i++)
        countP[pattern[i]-97]++;
    for(int  i=0; i<pattern.size(); i++)
        if((countP[pattern[i]-97]>countT[pattern[i]-97]))
            return "NOPE";
    return "YES";
}

int main()
{
    string text = "";
    string pattern = "ykzexfqiudtwvpjnhgocasbmlr";
    cout<<anagramPattern(text, pattern);
}
